# coding=utf-8class WordSentiment(object):    rootpath = 'D:/Anaconda3/myproject/sentiment_analysis_BUAA/lexicon/'    # import os    # print(os.getcwd() ) # 获得当前工作目录    def __init__(self):        negRank1Set_File = open(self.rootpath+'neg_rank1_v2.txt', 'r',encoding='utf8')        self.negRank1Set = set(negRank1Set_File.read().split('\n'))        negRank2Set_File = open(self.rootpath+'neg_rank2_v2.txt', 'r',encoding='utf8')        self.negRank2Set = set(negRank2Set_File.read().split('\n'))        negRank3Set_File = open(self.rootpath+'neg_rank3_v2.txt', 'r',encoding='utf8')        self.negRank3Set = set(negRank3Set_File.read().split('\n'))        negRank4Set_File = open(self.rootpath+'neg_rank4_v2.txt', 'r',encoding='utf8')        self.negRank4Set = set(negRank4Set_File.read().split('\n'))        negRank5Set_File = open(self.rootpath+'neg_rank5_v2.txt', 'r',encoding='utf8')        self.negRank5Set = set(negRank5Set_File.read().split('\n'))        posRank1Set_File = open(self.rootpath+'pos_rank1_v2.txt', 'r',encoding='utf8')        self.posRank1Set = set(posRank1Set_File.read().split('\n'))        posRank2Set_File = open(self.rootpath+'pos_rank2_v2.txt', 'r',encoding='utf8')        self.posRank2Set = set(posRank2Set_File.read().split('\n'))        posRank3Set_File = open(self.rootpath+'pos_rank3_v2.txt', 'r',encoding='utf8')        self.posRank3Set = set(posRank3Set_File.read().split('\n'))        posRank4Set_File = open(self.rootpath+'pos_rank4_v2.txt', 'r',encoding='utf8')        self.posRank4Set = set(posRank4Set_File.read().split('\n'))        posRank5Set_File = open(self.rootpath+'pos_rank5_v2.txt', 'r',encoding='utf8')        self.posRank5Set = set(posRank5Set_File.read().split('\n'))        negationSet_File = open(self.rootpath+'negation.txt', 'r',encoding='utf8')        self.negationSet = set(negationSet_File.read().split('\n'))        intensifierSet_File = open(self.rootpath+'intensifier.txt', 'r',encoding='utf8')        self.intensifierSet = set(intensifierSet_File.read().split('\n'))        diminisherSet_File = open(self.rootpath+'diminisher.txt', 'r',encoding='utf8')        self.diminisherSet = set(diminisherSet_File.read().split('\n'))        ironySet_File = open(self.rootpath+'irony.txt', 'r',encoding='utf8')        self.ironySet = set(ironySet_File.read().split('\n'))    def isWord(self, word):        if word in self.negRank1Set: return -1        if word in self.negRank2Set: return -2        if word in self.negRank3Set: return -3        if word in self.negRank4Set: return -4        if word in self.negRank5Set: return -5        if word in self.posRank1Set: return 1        if word in self.posRank2Set: return 2        if word in self.posRank3Set: return 3        if word in self.posRank4Set: return 4        if word in self.posRank5Set: return 5        return 0    def isNegation(self, word):        if word in self.negationSet:            return True        else:            return False    def isIntensifier(self, word):        if word in self.intensifierSet:            return True        else:            return False    def isDiminisher(self, word):        if word in self.diminisherSet:            return True        else:            return False    def isIrony(self, word):        if word in self.ironySet:            return True        else:            return False